	opt.value.body = cur
} else {
	Object.assign(opt.value, cur)
}
</script>

<template>
	<svg
		xmlns="http://www.w3.org/2000/svg"
		xmlns:xlink="http://www.w3.org/1999/xlink"
		aria-hidden="true" role="img" width="1em" height="1em"
		:viewBox="`0 0 ${ opt.width } ${ opt.height }`"
		v-bind="instance.attrs"
		v-html="opt.body">
	</svg>
</template>
